            tocLink.href = '#' + el.id;
            tocLink.text = el.innerText;
            tocItem.appendChild(tocLink);
            tocList.appendChild(tocItem);
        })
        document.getElementById('toc').appendChild(tocList);

        var titlesToLink = document.querySelectorAll('h2[id], h3[id], h4[id]');
